// fruits provider function that returns a component that contains the Fruits and FruitsCount components
import React, { useState, useEffect, useContext } from 'react';

import FruitsContext from './FruitsContext';

export default function FruitsProvider({ children }) {
    const initialState = []; //['apple', 'banana', 'cherry'];
    // const { fruits, setFruits } = useContext(FruitsContext);

    // const [fruits, setFruits] = useState(initialState);

    useEffect(() => {
        const [fruits, setFruits] = useState(initialState); // Initialize with null or an appropriate placeholder
        // Check if fruits are already initialized
        if (!fruits) {
            // Simulate fetching initial fruits data
            const fetchFruits = async () => {
                // const initialFruits = await fetchInitialFruits();
                setFruits(initialState);
            };

            fetchFruits();
        }
    }, [fruits]); // Only run this effect if fruits is null

    return (
        <FruitsContext.Provider value={{ fruits, setFruits }}>
            {children}
        </FruitsContext.Provider>
    );
}
